sample_queries:
-- Get latest year data
SELECT * FROM master.indicators
WHERE year = (SELECT MAX(year) FROM master.indicators);

-- Get data for specific country
SELECT * FROM master.indicators
WHERE country_id = 'USA';

-- Get specific indicator data
SELECT * FROM master.indicators
WHERE indicator_id LIKE '1.%';

